#!/bin/bash
curl -s https://raw.githubusercontent.com/0xGutso/logo/refs/heads/main/logo.sh | bash sleep 3 if ! command -v docker &> /dev/null; then sudo apt update && sudo apt upgrade -y && sudo apt install -y docker.io && sudo usermod -aG docker $USER && newgrp docker && echo "Docker yüklendi ve kullanıcı grubuna eklendi."; else echo "Docker zaten cihazda kurulu."; fi && curl --proto '=https' --tlsv1.2 -sSf https://sh.rustup.rs | sh -s -- -y && source $HOME/.cargo/env && rustc --version && cargo --version && echo 'source $HOME/.cargo/env' >> ~/.bashrc && curl -s https://raw.githubusercontent.com/0xGutso/soundness-layer/refs/heads/main/soundness-cli.sh | bash && source /root/.bashrc && curl -s https://raw.githubusercontent.com/0xGutso/soundness-layer/refs/heads/main/install_soundness.sh -o install_soundness.sh && chmod +x install_soundness.sh && ./install_soundness.sh && soundness-cli generate-key --name my-key